template <size_t _Count>
struct _TupleViewMaker {
  template <class _Struct>
  static constexpr auto view(_Struct&& s) {
    if constexpr (is_n_args_braces_constructible<std::decay_t<_Struct>,
                                                 _Count>()) {
      return __tuple_view_impl_n(s, _FieldCount<_Count>{});
    } else {
      return _TupleViewMaker<_Count - 1>::view(std::forward<_Struct>(s));
    }
  }
};
template <>
struct _TupleViewMaker<0> {
  template <class _Struct>
  static constexpr auto view(_Struct&&) {
    return std::tie();
  }
};

template <typename Struct>
constexpr auto tuple_view(Struct&& value) {
  using namespace nstl;
  return _TupleViewMaker<100>::view(value);
}

template <class F, class Struct>
void for_each_field(Struct&& s, F&& f) {
  static_assert(std::is_aggregate_v<std::decay_t<Struct>>,
                "The given type must be aggregate type only");
  return tuple_for_each(tuple_view(s), f);
}

